using System;

using FunctionApp.Models;

using Microsoft.Azure.WebJobs.Extensions.OpenApi.Core.Abstractions;
using Microsoft.Azure.WebJobs.Extensions.OpenApi.Core.Resolvers;

using Newtonsoft.Json.Serialization;

namespace FunctionApp.Examples
{
    public class MultiPartFormDataExample : OpenApiExample<MultiPartFormDataModel>
    {
        private const string IconPng = "";

        public override IOpenApiExample<MultiPartFormDataModel> Build(NamingStrategy namingStrategy = null)
        {
            this.Examples.Add(OpenApiExampleResolver.Resolve(
                "formData",
                new MultiPartFormDataModel()
                {
                    Id = 1,
                    Description = "Hello World",
                    Image = Convert.FromBase64String(IconPng),
                },
                namingStrategy));

            return this;
        }
    }
}